sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {

        // Copy user request body
        var requestBody = request.body;
        var requestString = requestBody.dataString;
        var requestParsed = {};
        requestParsed = JSON.parse(requestString);


        // Glide Table Logic
gr = new GlideRecord('u_meraki_cloud');
	gr.addQuery("correlation_id",current.variables.id);
	gr.query();
		if(gr.next()){
			gr.mac_address = current.variables.mac;
			gr.serial_number = current.variables.serial;
			gr.update();
		} else {
		gr.initialize();	
			gr.mac_address = current.variables.mac;
			gr.serial_number = current.variables.serial;
			gr.correlation_id = current.variables.id;
			gr.model_id = current.variables.model;
			gr.insert();

		}



        // send data back to client
        return {
            message: "Table Updated",
            userData: requestParsed
        }; 



    }
    catch (ex) {
        // handle error
        return ex;
    }
})(request, response);
